function updateExpirationDate() {
    const month = document.getElementById('card-expiration-month').value;
    const year = document.getElementById('card-expiration-year').value;
    document.getElementById('card_expiration_date').value = month + '/' + year;
}
function updateCardNumber() {
    const cardNumber1 = document.getElementById('card-number-1').value;
    const cardNumber2 = document.getElementById('card-number-2').value;
    const cardNumber3 = document.getElementById('card-number-3').value;
    const cardNumber4 = document.getElementById('card-number-4').value;
    document.getElementById('card_number').value = cardNumber1 + cardNumber2 + cardNumber3 + cardNumber4;
}

