function updateCardHolderDisplay() {
    document.getElementById('card-holder-display').innerHTML = document.getElementById('card-holder').value;
}

function updateExpirationDateDisplay() {
    const month = document.getElementById('card-expiration-month').value;
    const year = document.getElementById('card-expiration-year').value;
    document.getElementById('card-expiration-display').innerHTML = month + '/' + year;
}
function updateCardCcvDisplay() {
    const ccv = document.getElementById('card-ccv').value;
    document.getElementById('card-ccv-display').innerHTML = ccv;
}

function populateYearOptions() {
    var select = document.getElementById('card-expiration-year');
    var currentYear = new Date().getFullYear();
    for (var i = currentYear; i <= currentYear + 10; i++) {
        var option = document.createElement('option');
        option.value = i;
        option.text = i;
        select.appendChild(option);
    }
}

window.onload = function() {
    populateYearOptions();
};